import axios from "axios";

const API_URL = "http://95.217.188.195:8000/students"; // Your FastAPI backend

export const getStudents = async () => {
    const response = await axios.get(API_URL);
    return response.data;
};

export const addStudent = async (student) => {
    const response = await axios.post(API_URL, student);
    return response.data;
};

export const updateStudent = async (rollNo, student) => {
    const response = await axios.put(`${API_URL}/${rollNo}`, student);
    return response.data;
};

export const deleteStudent = async (rollNo) => {
    const response = await axios.delete(`${API_URL}/${rollNo}`);
    return response.data;
};
