import React, { useState } from "react";
import { addStudent } from "../api";

const AddStudent = ({ onStudentAdded }) => {
    const [student, setStudent] = useState({
        name: "",
        roll_no: "",
        age: "",
        gender: "",
        class_name: ""
    });

    const handleChange = (e) => {
        setStudent({ ...student, [e.target.name]: e.target.value });
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        await addStudent(student);
        onStudentAdded(); // Refresh student list
    };

    return (
        <div className="container mt-4">
            <h3>Add Student</h3>
            <form onSubmit={handleSubmit}>
                <input type="text" name="name" placeholder="Name" onChange={handleChange} required />
                <input type="number" name="roll_no" placeholder="Roll No" onChange={handleChange} required />
                <input type="number" name="age" placeholder="Age" onChange={handleChange} />
                <input type="text" name="gender" placeholder="Gender" onChange={handleChange} />
                <input type="text" name="class_name" placeholder="Class Name" onChange={handleChange} />
                <button type="submit">Add Student</button>
            </form>
        </div>
    );
};

export default AddStudent;
