import React from "react";

const StudentForm = ({ student, onChange }) => {
  return (
    <div className="form-panel">
      <label>Student ID</label>
      <input name="id" value={student.id} onChange={onChange} />

      <label>Firstname</label>
      <input name="firstname" value={student.firstname} onChange={onChange} />

      <label>Surname</label>
      <input name="surname" value={student.surname} onChange={onChange} />

      <label>Address</label>
      <input name="address" value={student.address} onChange={onChange} />

      <label>Gender</label>
      <select name="gender" value={student.gender} onChange={onChange}>
        <option value="Male">Male</option>
        <option value="Female">Female</option>
      </select>

      <label>Mobile</label>
      <input name="mobile" value={student.mobile} onChange={onChange} />

      {/* Subjects (toggle with radio/selects or checkboxes) */}
      {["Maths", "Games", "DataSci", "Graphic", "Database", "Science", "English", "Analysis"].map(subject => (
        <div key={subject}>
          <label>{subject}</label>
          <select name={subject.toLowerCase()} onChange={onChange} value={student[subject.toLowerCase()] || "No"}>
            <option value="Yes">Yes</option>
            <option value="No">No</option>
          </select>
        </div>
      ))}
    </div>
  );
};

export default StudentForm;
