import React, { useState, useEffect } from "react";
import StudentForm from "./StudentForm";
import StudentTable from "./StudentTable";
import { getStudents, addStudent, deleteStudent } from "../api";

const Dashboard = () => {
  const [details, setDetails] = useState({
    id: "", firstname: "", surname: "", address: "", gender: "Male", mobile: ""
  });

  const [subjects, setSubjects] = useState({
    maths: "No", games: "No", datasci: "No", graphic: "No",
    database: "No", science: "No", english: "Incomplete", analysis: "No"
  });

  const [students, setStudents] = useState([]);

  useEffect(() => {
    fetchStudents();
  }, []);

  const fetchStudents = async () => {
    try {
      const data = await getStudents();
      setStudents(data);
    } catch (error) {
      console.error("Error fetching students:", error);
    }
  };

  const handleDetailChange = (e) => {
    setDetails({ ...details, [e.target.name]: e.target.value });
  };

  const handleSubjectChange = (e) => {
    setSubjects({ ...subjects, [e.target.name]: e.target.value });
  };

  const handleAdd = async () => {
    const newStudent = { ...details, ...subjects };
    try {
      await addStudent(newStudent);
      fetchStudents();
      handleClear();
    } catch (error) {
      console.error("Error adding student:", error);
    }
  };

  const handleDelete = async (id) => {
  try {
    await deleteStudent(id);
    fetchStudents();
  } catch (error) {
    console.error("Error deleting student:", error);
  }
};


  const handleClear = () => {
    setDetails({
      id: "", firstname: "", surname: "", address: "", gender: "Male", mobile: ""
    });
    setSubjects({
      maths: "No", games: "No", datasci: "No", graphic: "No",
      database: "No", science: "No", english: "Incomplete", analysis: "No"
    });
  };

  return (
    <div className="dashboard" style={{ padding: "20px" }}>
      <h1 style={{ textAlign: "center" }}>Student Database Management System</h1>

      <div className="form-sections" style={{ display: "flex", gap: "40px", marginBottom: "20px" }}>
        <div style={{ flex: 1, border: "2px solid green", padding: "20px" }}>
          <h3>Student Details</h3>
          <StudentForm data={details} onChange={handleDetailChange} section="details" />
        </div>

        <div style={{ flex: 1, border: "2px solid green", padding: "20px" }}>
          <h3>Subjects</h3>
          <StudentForm data={subjects} onChange={handleSubjectChange} section="subjects" />
        </div>
      </div>

      <div className="action-buttons" style={{ marginBottom: "20px", textAlign: "center" }}>
        <button onClick={handleAdd}>Add New</button>{" "}
        <button onClick={fetchStudents}>Reset</button>{" "}
        <button onClick={handleClear}>Clear</button>{" "}
        <button onClick={() => window.print()}>Print</button>
      </div>

      <div style={{ border: "2px solid green", padding: "10px" }}>
        <StudentTable students={students} onDelete={handleDelete} />
      </div>
    </div>
  );
};

export default Dashboard;
