import React, { useState, useEffect } from "react";
import StudentForm from "./StudentForm";
import StudentTable from "./StudentTable";
import { getStudents, addStudent, deleteStudent } from "../api";

const Dashboard = () => {
  const [student, setStudent] = useState({
    id: "", firstname: "", surname: "", address: "", gender: "Male", mobile: "",
    maths: "Yes", games: "Yes", datasci: "Yes", graphic: "Yes",
    database: "Yes", science: "Yes", english: "Complete", analysis: "Yes"
  });
  const [students, setStudents] = useState([]);

  useEffect(() => {
    fetchStudents();
  }, []);

  const fetchStudents = async () => {
    const data = await getStudents();
    setStudents(data);
  };

  const handleChange = (e) => {
    setStudent({ ...student, [e.target.name]: e.target.value });
  };

  const handleAdd = async () => {
    await addStudent(student);
    fetchStudents();
  };

  const handleDelete = async (id) => {
    await deleteStudent(id);
    fetchStudents();
  };

  return (
    <div className="dashboard">
      <h1>Student Database Management System</h1>
      <div className="main-content">
        <StudentForm student={student} onChange={handleChange} />
        <StudentTable students={students} onDelete={handleDelete} />
      </div>
      <div className="action-buttons">
        <button onClick={handleAdd}>Add New</button>
        <button onClick={fetchStudents}>Reset</button>
        <button onClick={() => setStudent({})}>Clear</button>
        <button onClick={() => window.print()}>Print</button>
      </div>
    </div>
  );
};

export default Dashboard;
