import React, { useState } from "react";
import { addStudent } from "../api";

const AddStudent = ({ onStudentAdded }) => {
    const [student, setStudent] = useState({
        name: "",
        roll_no: "",
        age: "",
        gender: "",
        class_name: ""
    });

    const handleChange = (e) => {
        setStudent({ ...student, [e.target.name]: e.target.value });
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        await addStudent(student);
        onStudentAdded(); // Refresh student list
    };

    return (
        <div className="card p-4 bg-light">
            <h3 className="text-center">Add Student</h3>
            <form onSubmit={handleSubmit}>
                <input className="form-control mb-2" type="text" name="name" placeholder="Name" onChange={handleChange} required />
                <input className="form-control mb-2" type="number" name="roll_no" placeholder="Roll No" onChange={handleChange} required />
                <button className="btn btn-primary w-100">Add Student</button>
            </form>
        </div>
    );
};

export default AddStudent;
