import React, { useState, useEffect } from "react";
import { getStudents, deleteStudent } from "../api";
import AddStudent from "./AddStudent";

const StudentList = () => {
    const [students, setStudents] = useState([]);

    useEffect(() => {
        fetchStudents();
    }, []);

    const fetchStudents = async () => {
        const data = await getStudents();
        setStudents(data);
    };

    const handleDelete = async (rollNo) => {
        await deleteStudent(rollNo);
        fetchStudents();  // Refresh student list
    };

    return (
        <div className="container mt-4">
            <h2 className="text-center">Student List</h2>
            <AddStudent onStudentAdded={fetchStudents} />
            <ul className="list-group">
                {students.map(student => (
                    <li key={student.id} className="list-group-item bg-dark text-white">
                        <strong>{student.name}</strong> - Roll No: {student.roll_no}
                        <button className="btn btn-danger btn-sm float-end" onClick={() => handleDelete(student.roll_no)}>
                            Delete
                        </button>
                    </li>
                ))}
            </ul>
        </div>
    );
};

export default StudentList;
